settings.custom_css:
.title {
    font-size: 36px;
}

ul.tech-specs {
margin: 0;
font-size: 14px;
}
ul.tech-specs li {
list-style: none;
width: 50%;
display: block;
float: left;
font-weight: 500;
margin-bottom: 10px;
}
ul.tech-specs li span {
font-weight: 300;
width: 100px;
display: block;
float: left;
}
ul.tech-specs::after {
content:"";
clear: both;
display: block;
}
ul.tech-specs {
margin: 0;
font-size: 16px;
}
ul.tech-specs li {
list-style: none;
width: 50%;
display: block;
float: left;
font-weight: 700;
margin-bottom: 10px;
}
ul.tech-specs li span {
font-weight: 300;
width: 100px;
display: block;
float: left;
}
ul.tech-specs::after {
content:"";
clear: both;
display: block;
}

@media only screen and (max-width: 900px) {
 ul.tech-specs li {
width: 10%;
display: block;
float: none;
}
}

.product__information .money {
font-size: 32px;
}
